pragma solidity ^0.8.24;

library Errors {
    error ZeroAmount();
    error NotPayer();
    error NotPayee();
    error AlreadyFunded();
    error NotFunded();
    error AlreadyApproved();
    error NotApproved();
    error DeadlineNotReached();
    error DeadlinePassed();
}

